mapper.select:
SELECT user.id, user.user_name, user.password, role.role_code, menu.perms FROM t_user user left join t_user_role userRole on user.id=userRole.user_id left join t_role role on role.id=userRole.role_id left join t_role_menu roleMenu on role.id=roleMenu.role_id left join t_menu menu on menu.id=roleMenu.menu_id
and menu.type=#{menuType}
AND user.user_name=#{userName}
